module.exports = function(grunt) {
  grunt.registerTask('include-plugins', 'Save requirejs dependencies to config', function() {
    if (grunt.option('no-include-plugins')) {
      grunt.log.writeln('Skipping include of plugins');
      return;
    }
    var requirejsConfig = grunt.config.get('requirejs');
    var pluginsList = grunt.config.get('dom_munger').data.plugins;
    // Include plugins in requirejs
    for (var k in pluginsList) {
      requirejsConfig.compile.options.deps.push('plugins/' + pluginsList[k]);
    }
    grunt.config.set('requirejs', requirejsConfig);
    grunt.log.writeln('Require-js deps:');
    console.dir(requirejsConfig.compile.options.deps);
  });
};