/*global bookstrap: false */
define([
  'underscore',
  'plugins/Bookstrap.Navigation.Swipe',
  'plugins/Bookstrap.Navigation.Buttons'
],
function(_, Swipe, Buttons) {
    var BookstrapNavigationManager = function() {
      this.hasTouchSupport = bookstrap.hasTouchSupport;
      this.init();
    };

    BookstrapNavigationManager.prototype = {
      init: function() {
        var navigation;

        if (!this.hasTouchSupport) {
          navigation = new Buttons();
        } else {
          navigation = new Swipe();
        }
      }
    }

    return BookstrapNavigationManager;
});
