/*global bookstrap: false */
define([
  'underscore'
  ],
  function(_) {

    var SectionTabs = function() {
      _.bindAll(this);
      bookstrap.on('pageload', this.onPageLoad);
      $('html').addClass('has-section-tabs');
    };

    SectionTabs.prototype.onPageLoad = function(domPage) {
      var activeTabIndex = domPage.page.section || -1;
      $('nav.book-section-tabs').attr('data-section-active', activeTabIndex);
    };

    return SectionTabs;
});