/*global bookstrap: false */
define([
  'underscore'
  ],
  function(_) {

    var SamplePlugin = function() {
      bookstrap.on('pagecreated', _.bind(this.onPageCreated, this));
      bookstrap.on('pageload', _.bind(this.onPageLoad, this));
    };

    SamplePlugin.prototype.init = function() {
      console.log('Sample plugin: init');
    };

    SamplePlugin.prototype.onPageCreated = function(page) {
      console.log('Sample plugin: onPageCreated');
      page.$el.css('border', '1px solid red');
    };

    SamplePlugin.prototype.onPageLoad = function(page) {
      console.log('Sample plugin: onPageLoad');
      page.$el.prepend('<p>Hello world (from SamplePlugin)</p>');
    };

    return SamplePlugin;
});